fn floor(instructions: &str) -> (i32, i32) {
    let mut position = 0;
    let mut i = 0;
    let mut basement = -1;

    for c in instructions.chars() {
        i += 1;

        if c == '(' {
            position += 1;
        } else if c == ')' {
            position -= 1;
        }

        if position == -1 && basement < 0 {
            basement = i;
        }
    }

    (position, basement)
}
